import "./HomeTop.css";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

import HorizontalCard from "../Cards/HorizontalCard";
import Carousel from "../Carousel/Carousel";

const HomeTop = () => {
  //asas
  const [data, setData] = useState();
  let { id } = useParams();
  useEffect(() => {
    fetch(`http://localhost:5050/api/movies/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setData(data);
      });
  }, [id]);
  // asas
  const funkcija = () => {
    const linkKodas = data.videos.trailers;
    const iskirptasKodas = linkKodas.map((iskarpa) => iskarpa.slice(32, 43));
    const Link = `https://www.youtube.com/embed/${iskirptasKodas[0]}`;
    console.log(data);
    console.log(linkKodas);
    console.log(iskirptasKodas);
    console.log(Link);
    return Link;
  };
  return (
    <>
      <div className="mainDivHome">
        <div className="leftDiv">
          <iframe
            width="100%"
            height="100%"
            src={funkcija()}
            title={data.title}
            frameborder="0"
            allowfullscreen
          ></iframe>
        </div>
        <div className="rightDiv">
          {data.map((cards) => (
            <HorizontalCard data={cards} />
          ))}
        </div>
      </div>
      <Carousel data={data} />
    </>
  );
};
export default HomeTop;
