import "./TrailerZone.css";

const Trailer = () => {
  const linkKodas = data.map((linkas) => linkas.videos.trailers);
  const iskirptasKodas = linkKodas.map((iskarpa) => iskarpa[0].slice(32, 43));
  const Link = `https://www.youtube.com/embed/${iskirptasKodas[0]}`;
  const [data, setData] = useState();
  let { id } = useParams();
  useEffect(() => {
    fetch(`http://localhost:5050/api/movies/${id}`)
      .then((res) => res.json())
      // .then((data) => setData(data));
      .then((data) => {
        setData(data);
      });
  }, [id]);
  return (
    <>
      <section className="trailerMainSection">
        <div className="trailerLeftDiv">
          <div className="trailerDivVirsus">
            <button className="trailerUzdarytiKnopke">
              <span style={{ fontSize: "1.5rem" }}>X</span>{" "}
              <span style={{ fontWeight: 500 }}>Close</span>
            </button>
            <button className="trailerDalintisKnopke">
              <i class="bi bi-share-fill"></i>
            </button>
          </div>
          <iframe
            width="840px"
            height="450px"
            src={Link}
            title={data.title}
            frameborder="0"
            allowfullscreen
          ></iframe>
        </div>
        <div className="trailerRightDiv">
          <h3
            style={{
              color: "white",
            }}
          >
            {data.title}
          </h3>
        </div>
      </section>
    </>
  );
};
export default Trailer;
