import "./HomeTop.css";
import HorizontalCard from "../Cards/HorizontalCard";
import Carousel from "../Carousel/Carousel";

const HomeTop = ({ data }) => {
  const linkKodas = data.map((linkas) => linkas.videos.trailers);
  const iskirptasKodas = linkKodas.map((iskarpa) => iskarpa[0].slice(32, 43));
  const Link = `https://www.youtube.com/embed/${iskirptasKodas[0]}`;

  return (
    <>
      <div className="mainDivHome">
        <div className="leftDiv">
          <iframe
            width="100%"
            height="100%"
            src={Link}
            title={data.title}
            frameborder="0"
            allowfullscreen
          ></iframe>
        </div>
        <div className="rightDiv">
          {data.map((cards) => (
            <HorizontalCard data={cards} />
          ))}
        </div>
      </div>
      <Carousel data={data} />
    </>
  );
};
export default HomeTop;
