import "./App.css";
import Navbar from "./components/Navbar/Navbar";
import { Route, Routes } from "react-router-dom";
import { useEffect, useState, useNavigate } from "react";

import MovieById from "./components/Movie/MovieById";
import HomeTop from "./components/HomeTop/HomeTop";
import Trailer from "./components/Trailer/Trailer";
import Footer from "./components/Footer/Footer";
import PageNotFound from "./components/PageNotFound";
import AddMovie from "./components/addMovie/AddMovie";

function App() {
  const [data, setData] = useState([]);
  let navigate = useNavigate();
  // connection to backend

  const [movies, setMovies] = useState([]);
  useEffect(() => {
    fetch(`/api/movies/`)
      .then((res) => res.json())
      .then((data) => {
        setMovies(data);
      });
  }, []);
  // add movie to IMDb
  /// postinti pets
  const addMovie = async (e) => {
    e.preventDefault();
    const movie = {
      name: e.target.vardas.value,
      dob: e.target.gimimoData.value,
      client_email: e.target.elPastas.value,
      archyvuotas: false,
    };
    await fetch("/api/movies", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(movie),
    })
      .then(() => setData([...data, movie]))
      .then(() => navigate("/", { replace: true }));
  };

  return (
    <>
      <Navbar />
      <Routes>
        <Route path="/addmovie" element={<AddMovie addMovie={addMovie} />} />
        <Route path="*" element={<PageNotFound />} />
        <Route path="/" element={<HomeTop data={movies} />} />
        <Route path="/movie/:id" element={<MovieById />} />
        <Route path="/trailer" element={<Trailer data={movies} />} />
        <Route path="/trailer/:id" element={<Trailer />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
