import "./TrailerZone.css";

const Trailer = ({ data }) => {
  const linkKodas = data.map((linkas) => linkas.videos.trailers);
  const iskirptasKodas = linkKodas.map((iskarpa) => iskarpa[0].slice(32, 43));
  const Link = `https://www.youtube.com/embed/${iskirptasKodas[0]}`;
  return (
    <>
      <section className="trailerMainSection">
        <button>
          <span>X</span> <span>Close</span>
        </button>
        <iframe
          width="800px"
          height="420px"
          src={Link}
          title={data.title}
          frameborder="0"
          allowfullscreen
        ></iframe>
        <div></div>
      </section>
    </>
  );
};
export default Trailer;
